	class DocElement extends StatefulComponent{
		sentData(){
			const values = this.el.getBoundingClientRect()
			const remH = this.remRef.getBoundingClientRect().height
			const w = getWindowRect()
			const ww = w.width
			const wh = w.height
			const b = documentManager.body().clientWidth
			const ss = w - b
			const width = ww
			const height = wh
			if(width!=this.width||height!=this.height){
				this.props.onWResize && this.props.onWResize("change",`${width},${height},${remH}`)
				this.width = width
				this.height = height
			}
		}
		onResize(){
			if(!this.el || !this.remRef) return				
			const isSibling = Branches.isSibling(this.ctx)
			if(isSibling) return			
			if(this.unmounted) return
			this.sentData()			
		}
		componentWillUnmount(){
			this.unmounted = true			
			if(this.resizeL) this.resizeL = this.resizeL.unreg()
			this.remRef && this.remRef.parentElement.removeChild(this.remRef)
		}
		initListener(){			
			const isSibling = Branches.isSibling(this.ctx)
			if(isSibling) return
			if(this.props.onWResize && this.el && this.remRef && !this.resizeL) {
				//log(`init listener`)
				this.sentData()
				this.resizeL = resizeListener.reg(this.onResize)
			}
		}
		componentDidUpdate(prevProps){
			this.initListener()
		}
		componentDidMount(){
			if(!this.el) return
			const node = documentManager.body().querySelector("#dev-content")
			const nodeOld = documentManager.body().querySelector("#content")			
			while (node&&node.hasChildNodes()) node.removeChild(node.lastChild)						
			while (nodeOld&&nodeOld.hasChildNodes()) nodeOld.removeChild(nodeOld.lastChild)
			const doc = this.el.ownerDocument
			this.remRef = doc.createElement("div")	
			Object.assign(this.remRef.style,{height:"1em", position:"absolute",zIndex:"-1", top:"0px"})
			doc.body.appendChild(this.remRef)
			this.ctx = rootCtx(this.props.ctx)
			this.initListener()			
		}
		render(){			
			const isSibling = Branches.isSibling(this.ctx)						
			return $("div",{key:"1",className:"docRoot",style:this.props.style,ref:ref=>this.el=ref},this.props.children)						
		}
	}
